
public class Test9x9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//	for (int i = 0; i <= 9; i++) {

//		int i = 1;
		for (int i = 1; i <= 9; i++) {
			for (int j = 1; j <= 9; j++) {
				System.out.print(i + "x" + j + "=" + i * j + "\t"); // ln for line換行

			}
			System.out.println(); //預設為換行
		}

	}

}
